Request.ResponseData:
            function initExtJS(context) {

    Ext.GLOBAL_CONTEXT = context;



    Ext.require([

        'Ext.grid.feature.GroupingSummary'

    ]);



    Ext.Loader.config.disableCaching = false;



    Ext.application({

        appFolder: Ext.GLOBAL_CONTEXT + 'resources/extjs/app',

        name: 'ZeroSite',



        views: ['DetailedReportWindow', 'DetailedReport', 'Report', 'Viewport', 'PieChart'],

        models: ['Report', 'SummaryReport', 'DetailedReport'],

        controllers: ['Report']

    });

}

            Ext.define('ZeroSite.controller.Report', {
    extend: 'Ext.app.Controller',

    views: ['DetailedReportWindow', 'DetailedReport', 'Report', 'SummaryReport', 'Viewport'],

    models: ['Report', 'DetailedReport'],
    stores: ['ReportCredit', 'ReportInflow', 'ReportOutFlow', 'ReportSummary', 'DetailedReport'],

    refs: [
        {
            ref: 'summaryReport',
            selector: '#summaryReport'
        }
    ],

    viewport: null,
    detailedReport: null,

    init: function() {
        this.viewport = Ext.create('ZeroSite.view.Viewport', { renderTo: 'report' });
        this.detailedReport = Ext.create('ZeroSite.view.DetailedReportWindow', { renderTo: 'report' });

        var container = Ext.create('Ext.container.Container', {
            layout: 'fit',
//            width: 800,
            height: 600,
            renderTo: 'charts'
        });

        var chart = Ext.create('ZeroSite.view.PieChart', { store: this.getReportOutFlowStore() });
        container.add(chart);

        this.viewport.show();
        container.show();

        this.control({
            'report': {
                moreInfo: this.onMoreInfoRender
            }
        });
    },

    onMoreInfoRender: function(item) {
        this.detailedReport.hide();
        this.detailedReport.alignTo(item.cell, 'tr-tl', [-60, 0]);
        var data = item.record.data;
        this.detailedReport.down('detailedreport').getStore().load({ id: data.id });
        this.detailedReport.setTitle('Detailed Report for ' + data.category);
        this.detailedReport.show();
    }
});

            Ext.define('ZeroSite.model.Report', {
    extend: 'Ext.data.Model',

    fields: [
        { name: 'id', type: 'int' },
        { name: 'category', type: 'string' },
        { name: 'amount', type: 'float' },
        { name: 'type', type: 'string' }
    ]
});

            Ext.define('ZeroSite.store.ReportOutFlow', {
    extend: 'Ext.data.Store',
    model: 'ZeroSite.model.Report',

    autoLoad: true,

    proxy: {
        type: 'ajax',
        url: Ext.GLOBAL_CONTEXT + 'bank/money-map-get-spendings-by-type.html',
        actionMethods: { read: 'POST' },
        extraParams: {
            groupType: 'OUTFLOW'
        },
        reader: {
            type: 'json',
            root: 'data'
        },
        filterParam: undefined,
        groupParam: undefined,
        pageParam: undefined,
        startParam: undefined,
        sortParam: undefined,
        limitParam: undefined
    },

    groupField: 'type'
});

            Ext.define('ZeroSite.view.Report', {
    extend: 'Ext.grid.Panel',
    alias: 'widget.report',

    features: [
        {
            groupHeaderTpl: 'Subject: {name}',
            ftype: 'groupingsummary'
        }
    ],

    initComponent: function() {
        var reportGrid = this;
        this.addEvents(['moreInfo']);

        this.columns = [
            {
                header: 'Category',
                dataIndex: 'category',
                flex: 1,
                summaryRenderer: function(value, summaryData, dataIndex) {
                    return '<b>Total</b>';
                }
            },
            {
                header: 'Amount',
                dataIndex: 'amount',
                flex: 1,
                renderer: function(value) {
                    return Ext.util.Format.usMoney(Math.abs(value));
                },
                summaryType: 'sum',
                summaryRenderer: function(value, summaryData, dataIndex) {
                    return Ext.String.format('<b>' + Ext.util.Format.usMoney(Math.abs(value)) + '</b>');
                }
            },
            {
                xtype: 'actioncolumn',
                width: 50,
                items: [
                    {
                        text: 'More Info',
                        icon: Ext.GLOBAL_CONTEXT + 'resources/img/more_info.png',
                        handler: function(view, rowIndex, colIndex, item, e, record) {
                            reportGrid.fireEvent('moreInfo', { cell: view.getCellByPosition({row: rowIndex, column: colIndex}), record: record });
                        }
                    }
                ]
            }
        ];

        this.callParent(arguments);
    }
});

